import Item from "./Items/Item";
import {useEffect, useState} from "react";

const MainPage = ({items, deleteItem}) => {

    // let [state, setState] = useState([...items])
    //
    // const sortByName = () => {
    //     debugger
    //     state.sort(function (a, b) {
    //         if (a.name > b.name) {
    //             return 1
    //         }
    //         if (a.name < b.name) {
    //             return -1
    //         }
    //         return 0
    //     })
    //   setState(state)
    // }

    const [data, setData] = useState([])
    const [sortType,setSortType] = useState('count')
    useEffect(() => {
        const sortArray = (type) => {
            const types = {
                name: 'name',
                count: 'count'
            }
            let sortProperty = types[type]
            const sorted = [...items].sort((a, b) => a[sortProperty] - b[sortProperty])
            setData(sorted)
        }
    }, [sortType])


    return (
        <div>
            <select onChange={(e) => setSortType(e.currentTarget.value)}>
                <option value="name">Name</option>
                <option value="count">Count</option>
            </select>
            {items.map(item => <Item key={item.id}
                                     id={item.id}
                                     imageUrl={item.imageUrl}
                                     count={item.count}
                                     name={item.name}
                                     size={item.size}
                                     weight={item.weight}
                                     deleteItem={deleteItem}
            />)}
        </div>
    )
}

export default MainPage
