export const itemsReducer = (state = initialState, action) => {
    switch (action.type) {
        case DELETE_ITEM :
            return {
                ...state,
                items: state.items.filter(item => item.id !== action.itemId)
            }
        case ADD_ITEM:
            let last = state.items.length - 1
            let item = {
                id: state.items[last] ? state.items[last].id + 1 : 1,
                imageUrl: 'https://images.unsplash.com/photo-1580910051074-3eb694886505?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8cGhvbmV8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80',
                name: action.data.name,
                count: action.data.count,
                size: {
                    width: action.data.width,
                    height: action.data.height
                },
                weight: action.data.weight,
                comments: [],
                description: action.data.description,
                collar: action.data.collar,
            }
            return {
                ...state,
                items: [...state.items, item]
            }
        case SET_ITEM:
            return {
                ...state,
                item: action.data

            }

        default:
            return state
    }
}

export const deleteItem = (itemId) => ({type: 'DELETE_ITEM', itemId})
export const addItem = (data) => ({type: 'ADD_ITEM', data})
export const setItem = (data) => ({type: 'SET_ITEM', data})
