import s from './Item.module.css'
import {NavLink} from "react-router-dom";
import {useState} from "react";

const Item = () => {

    const [items, setItems] = useState([
            {
                id: 1,
                imageUrl: 'https://images.unsplash.com/photo-1580910051074-3eb694886505?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8cGhvbmV8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80',
                name: 'Product name',
                count: 4,
                size: {
                    width: 200,
                    height: 200
                },
                weight: '200g',
                comments: [Comment, Comment]
            }
        ]
    )

    return (
        <div>
            <div>
                {items.map( item => {
                return  <table className={s.item}>
                        <tr>
                            <td><img src={item.imageUrl}/>
                            </td>
                            <td valign="top">
                                name: {item.name}
                                <br/>
                                description: {item.weight + '' + item.size.height + '' + item.size.height}
                                <br/>
                                count: {item.count}
                                <br/>
                                <NavLink to='/additionalInformation'>Additional information</NavLink>
                            </td>
                        </tr>
                    </table>
                })}


            </div>
        </div>
    )

}


export default Item
