import Item from "./Items/Item";
import {useEffect, useState} from "react";
import s from './MainPage.module.css'
import './modal.css'

const MainPage = ({items, deleteItem}) => {

    const [data, setData] = useState([])
    const [sortType, setSortType] = useState()

    useEffect(() => {
        debugger
        const sortArray = (type) => {
            const types = {
                name: 'name',
                count: 'count'
            }
            let sortProperty = types[type]
            if (sortProperty === 'name') {
                const sorted = [...items].sort((a, b) => a.name.localeCompare(b.name));
                setData(sorted)
            } else if (sortProperty === 'count') {
                const sorted = [...items].sort((a, b) => a[sortProperty] - b[sortProperty])
                setData(sorted)
            } else {
                return null
            }
        }
        sortArray(sortType)
    }, [sortType])
    useEffect(() => {
        setData(items)
    }, [items])

    let modal = document.getElementById('myModal')

    const ModalOn = () => {
            modal.style.display = "block";

    }
    const closeModal = () => {
            modal.style.display = "none";
    }
    modal.onclick = function (event) {
        if (event.target === modal) {
            modal.style.display = "none";
        }
    }

    return (
        <div>
            <button className={s.addProductButton} onClick={ModalOn}>Add Product</button>
            <select className={s.selectMode} onChange={(e) => setSortType(e.currentTarget.value)}>
                <option value="sort">Sort By:</option>
                <option value="name">Name</option>
                <option value="count">Count</option>
            </select>

            {data.map(item => <Item key={item.id}
                                    id={item.id}
                                    imageUrl={item.imageUrl}
                                    count={item.count}
                                    name={item.name}
                                    size={item.size}
                                    weight={item.weight}
                                    deleteItem={deleteItem}
            />)}



                <div id="myModal" className="modal">
                    <div className="modal-content">
                        <div className="modal-header">
                            <span onClick={closeModal} className="close">×</span>
                            <h2>Add Product</h2>
                        </div>
                        <div className="modal-body">
                            <input type="text"/>
                            <input type="text"/>
                            <input type="text"/>
                            <input type="text"/>
                        </div>
                        <div className="modal-footer">
                            <h3></h3>
                        </div>
                    </div>
                </div>
            </div>

    )
}

export default MainPage
