import Item from "./Items/Item";

const MainPage = ({items}) => {

    let state = items
    const sortByName = () => {
        debugger
        state.sort(function (a, b) {
            if (a.name > b.name) {
                return 1
            }
            if (a.name < b.name) {
                return -1
            }
            return 0
        })
    }

    return (
        <div>
            <div>
                <span >Name</span>
                <span onClick={sortByName} >Count</span>
            </div>
            {state.map(item => <Item key={item.id}
                                     imageUrl={item.imageUrl}
                                     count={item.count}
                                     name={item.name}
                                     size={item.size}
                                     weight={item.weight}
            />)}
        </div>
    )
}

export default MainPage
