import Item from "./Items/Item";
import {useState} from "react";

const MainPage = ({items,deleteItem}) => {

    let [state, setState] = useState([...items])

    const sortByName = () => {
        debugger
        state.sort(function (a, b) {
            if (a.name > b.name) {
                return 1
            }
            if (a.name < b.name) {
                return -1
            }
            return 0
        })
      setState(state)
    }

    return (
        <div>

            <div>
                <button onClick={sortByName}>Name</button>
                <button >Count</button>
            </div>
            {state.map(item => <Item key={item.id}
                                     id={item.id}
                                     imageUrl={item.imageUrl}
                                     count={item.count}
                                     name={item.name}
                                     size={item.size}
                                     weight={item.weight}
                                     deleteItem={deleteItem}
            />)}
        </div>
    )
}

export default MainPage
