import React from 'react'
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import {compose} from "redux";
import {addComment, deleteComment, setItem, updateItem} from "../../Redux/items-reducer";
import AddInformation from "./AddInformation";
import CommentsSection from "./Comments/CommentSection";

class AddInformationContainer extends React.Component {


    getItemId = () => {
        let itemId = this.props.match.params.itemId - 1
        this.props.setItem(itemId)
    }

    componentDidMount() {
        this.getItemId()
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.item !== this.props.item) {
            this.getItemId()
        }
    }

    render() {
        return (
            <>
                {this.props.item &&
                <div>
                    <AddInformation item={this.props.item} updateItem={updateItem}/>
                    <CommentsSection
                        comments={this.props.item.comments}
                        deleteComment={this.props.deleteComment}
                        addComment={this.props.addComment}
                    />
                </div>
                }

            </>
        )
    }
}

const mapStateToProps = (state) => ({
    item: state.itemsPage.item,
    items: state.itemsPage.items,
})


export default compose(connect(mapStateToProps, {setItem, deleteComment, addComment, updateItem}),
    withRouter)(AddInformationContainer)
