const DELETE_ITEM = 'DELETE_ITEM'
const SORT_ITEMS = 'SORT_ITEMS'
const initialState = {
    items: [{
        id: 1,
        imageUrl: 'https://images.unsplash.com/photo-1580910051074-3eb694886505?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8cGhvbmV8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80',
        name: 'c',
        count: 10,
        size: {
            width: 200,
            height: 200
        },
        weight: '200g',
        comments: [Comment, Comment]
    },
        {
            id: 2,
            imageUrl: 'https://images.unsplash.com/photo-1580910051074-3eb694886505?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8cGhvbmV8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80',
            name: 'b',
            count: 5,
            size: {
                width: 200,
                height: 200
            },
            weight: '200g',
            comments: [Comment, Comment]
        },
        {
            id: 7,
            imageUrl: 'https://images.unsplash.com/photo-1580910051074-3eb694886505?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8cGhvbmV8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80',
            name: 'a',
            count: 1,
            size: {
                width: 200,
                height: 200
            },
            weight: '200g',
            comments: [Comment, Comment]
        }
    ]

}

export const itemsReducer = (state = initialState, action) => {
    switch (action.type) {
        case DELETE_ITEM :
            return {
                ...state,
                items: state.items.filter(item => item.id !== action.itemId)
            }
        default:
            return state
    }
}

export const deleteItem = (itemId) => ({type: 'DELETE_ITEM', itemId})
export const sortItems = (itemId) => ({type: 'SORT_ITEMS'})
