import s from './Item.module.css'
import {NavLink} from "react-router-dom";

const Item = ({imageUrl, count, name, size, weight, id, deleteItem}) => {

    const confirmDelete = (id) => {
        let result = window.confirm('Are you sure?')
        if (result) {
            deleteItem(id)
        } else {
            return null
        }
    }

    return (
        <div>
            <div>
                <table className={s.item}>
                    <tbody>
                    <tr>
                        <td className={s.imgBlock}>
                            <img src={imageUrl} alt=''/>
                        </td>
                        <td valign="top" className={s.itemDescription}>
                            <div>
                                name: {name}
                            </div>
                            <div>
                                description: {weight + '' + size.height + '' + size.height}
                            </div>
                            <div>
                                count: {count}
                            </div>
                            <div>
                            <NavLink className={s.addInfLink} to={'/additionalInformation/' + id}>Additional
                                information</NavLink>
                            </div>
                        </td>
                        <td>
                            <button className={s.deleteButton} onClick={() => confirmDelete(id)}>X</button>
                        </td>
                    </tr>
                    </tbody>
                </table>

            </div>
        </div>
    )

}


export default Item
