import React from 'react'
import {connect} from "react-redux";
import MainPage from "./MainPage";
import {addItem, deleteItem} from "../../Redux/items-reducer";
class MainPageContainer extends React.Component {

    render() {
       return (
           <div>
            <MainPage items={this.props.items} deleteItem={this.props.deleteItem}/>
           </div>
       )
    }
}
const mapStateToProps = (state) => ({
    items: state.itemsPage.items
})



export default connect(mapStateToProps,{deleteItem, addItem})(MainPageContainer)
