            },
            weight: '200g',
            comments: [Comment, Comment]
        },
        {
            id: 3,
            imageUrl: 'https://images.unsplash.com/photo-1580910051074-3eb694886505?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8cGhvbmV8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80',
            name: 'Iphone12',
            count: 1,
            size: {
                width: 200,
                height: 200
            },
            weight: '200g',
            comments: [Comment, Comment]
        }
    ]

}

const DELETE_ITEM = 'DELETE_ITEM'

export const itemsReducer = (state = initialState, action) => {
    switch (action.type) {
        case DELETE_ITEM :
            return {
                ...state,
                items: state.items.filter(item => item.id !== action.id)
            }

        default:
            return state
    }
}

export const deleteItem = (itemId) => ({type: 'DELETE_ITEM', itemId})
