import Item from "./Items/Item";
import {useEffect, useState} from "react";
import s from './MainPage.module.css'
import './AddProductForm/AddProductForm.css'
import {Field, reduxForm} from "redux-form";


const AddProductForm = (props) => {

    return (

        <form onSubmit={props.handleSubmit}>
            <div>
                Name:<Field component={'input'} name={'name'} placeholder={'name'}/>
            </div>
            <div>
                Collar:<Field component={'input'} name={'collar'} placeholder={'collar'}/>
            </div>
            <div>
                Width:<Field component={'input'} name={'width'} placeholder={'width'}/>
            </div>
            <div>
                Height:<Field component={'input'} name={'height'} placeholder={'height'}/>
            </div>
            <div>
                Count:<Field component={'input'} name={'count'} placeholder={'count'}/>
            </div>
            <div>
                Weight:<Field component={'input'} name={'weight'} placeholder={'weight'}/>
            </div>
            <div>
                Height:<Field component={'input'} name={'height'} placeholder={'height'}/>
            </div>
            <button>Add Product</button>
        </form>

    )

}


const AddProductReduxForm = reduxForm({form: 'add-product'})(AddProductForm)


const MainPage = ({items, deleteItem, addItem}) => {

    const [data, setData] = useState([])
    const [sortType, setSortType] = useState()
    const [isAddFormOpen, setIsAddFormOpen] = useState(false)

    useEffect(() => {

        const sortArray = (type) => {
            const types = {
                name: 'name',
                count: 'count'
            }
            let sortProperty = types[type]
            if (sortProperty === 'name') {
                const sorted = [...items].sort((a, b) => a.name.localeCompare(b.name));
                setData(sorted)
            } else if (sortProperty === 'count') {
                const sorted = [...items].sort((a, b) => a[sortProperty] - b[sortProperty])
                setData(sorted)
            } else {
                return null
            }
        }
        sortArray(sortType)
    }, [sortType])
    useEffect(() => {
        setData(items)
    }, [items])


    let modal = document.getElementById('myModal')

    const ModalOn = () => {
        setIsAddFormOpen(true)
    }
    const onSubmit = (formData) => {
        addItem(formData)
        modal.style.display = "none";
    }
    const closeModal = () => {
        let modal = document.getElementById('myModal')
        modal.style.display = "none";
    }

    return (
        <div>
            <button className={s.addProductButton} onClick={ModalOn}>Add Product</button>
            <select className={s.selectMode} onChange={(e) => setSortType(e.currentTarget.value)}>
                <option value="sort">Sort By:</option>
                <option value="name">Name</option>
                <option value="count">Count</option>
            </select>

            {data.map(item => <Item key={item.id}
                                    id={item.id}
                                    imageUrl={item.imageUrl}
                                    count={item.count}
                                    name={item.name}
                                    size={item.size}
                                    weight={item.weight}
                                    deleteItem={deleteItem}
            />)}

            {isAddFormOpen &&
            <div id="myModal" className="modal">
                <div className="modal-content">
                    <div className="modal-header">
                        <span onClick={closeModal} className="close">Ã—</span>
                        <h2>Add Product</h2>
                    </div>
                    <div className="modal-body">
                        <AddProductReduxForm onSubmit={onSubmit}/>
                    </div>
                    <div className='modal-footer'>
                    </div>
                </div>
            </div>
            }

        </div>

    )
}


export default MainPage
