import Item from "./Items/Item";

const MainPage = ({items}) => {


    function sortByName(a, b, sortAsc = true) {
        var val1 = a.name;
        var val2 = b.name;
        let result = ((val1 < val2) ? -1 : ((val1 > val2) ? 1 : 0));
        if (!sortAsc) {
            result *= -1;
        }
        return result;
    }
    return (
        <div>
            <div>
                <span >Name</span>
                <div onClick={items.sort( sortByName(a,b, true))}>Count</div>
            </div>
            {items.map(item => <Item key={item.id}
                                     imageUrl={item.imageUrl}
                                     count={item.count}
                                     name={item.name}
                                     size={item.size}
                                     weight={item.weight}
            />)}
        </div>
    )
}

export default MainPage
