import React from 'react'
import {connect} from "react-redux";
import AddInformation from "./AddInformation";
import {withRouter} from "react-router-dom";
import {compose} from "redux";

class AddInformationContainer extends React.Component {

    state = {
        itemId: 0
    }

    getItemId = () => {
        let itemId = +this.props.match.params.itemId - 1
        this.setState({
            itemId: itemId
        })
    }
    componentDidMount() {
        this.getItemId()
        console.log(this.props.items[this.state.itemId].map( item =>    <AddInformation imgUrl={item.imgUrl}
                                                                     name={item.name}
                                                                     description={item.description}
                                                                     collar={item.collar}
                                                                     size={item.size}
                                                                     weight={item.weight} />))
    }


    render() {
       return(
           <>
                <AddInformation imgUrl={this.props.imgUrl}
                                name={this.props.name}
                                description={this.props.description}
                                collar={this.props.collar}
                                size={this.props.size}
                                weight={this.props.weight} />
           </>
       )
   }
}

const mapStateToProps = (state) => ({
    imgUrl: state.itemsPage.items.imageUrl,
    name: state.itemsPage.items.name,
    description: state.itemsPage.items.description,
    collar: state.itemsPage.items.collar,
    size: state.itemsPage.items.size,
    weight: state.itemsPage.items.weight,
    items: state.itemsPage.items


})
const mapDispatchToProps = (state) => {

}



export default compose(connect(mapStateToProps, mapDispatchToProps),
    withRouter)(AddInformationContainer)
