import Item from "./Items/Item";
import {useEffect, useState} from "react";
import s from './MainPage.module.css'
import './AddProductForm/AddProductForm.css'
import {Field, reduxForm} from "redux-form";


const AddProductForm = (props) => {

    const closeModal = () => {
        let modal = document.getElementById('myModal')
        modal.style.display = "none";
    }
    return (
        <div id="myModal" className="modal">

                    <form onSubmit={props.handleSubmit}>
                        <div>
                            <label>
                                Name:
                                <Field component={'input'} name={'name'} placeholder={'name'}/>
                            </label>
                        </div>
                        <div>
                            <label htmlFor="">
                                Collar:
                                <Field component={'input'} name={'collar'} placeholder={'collar'}/>
                            </label>
                        </div>
                        <div>
                        <label htmlFor="">
                            Width:
                            <Field component={'input'} name={'width'} placeholder={'width'}/>
                        </label>
                        </div>
                        <div>
                        <label htmlFor="">
                            Height:
                            <Field component={'input'} name={'height'} placeholder={'height'}/>
                        </label>
                        </div>
                        <button>Add Product</button>
                    </form>

        </div>
    )

}


const AddProductReduxForm = reduxForm({form: 'add-product'})(AddProductForm)


const MainPage = ({items, deleteItem}) => {

    const [data, setData] = useState([])
    const [sortType, setSortType] = useState()

    useEffect(() => {

        const sortArray = (type) => {
            const types = {
                name: 'name',
                count: 'count'
            }
            let sortProperty = types[type]
            if (sortProperty === 'name') {
                const sorted = [...items].sort((a, b) => a.name.localeCompare(b.name));
                setData(sorted)
            } else if (sortProperty === 'count') {
                const sorted = [...items].sort((a, b) => a[sortProperty] - b[sortProperty])
                setData(sorted)
            } else {
                return null
            }
        }
        sortArray(sortType)
    }, [sortType])
    useEffect(() => {
        setData(items)
    }, [items])


    let modal = document.getElementById('myModal')

    const ModalOn = () => {
        modal.style.display = "block";
    }


    return (
        <div>
            <button className={s.addProductButton} onClick={ModalOn}>Add Product</button>
            <select className={s.selectMode} onChange={(e) => setSortType(e.currentTarget.value)}>
                <option value="sort">Sort By:</option>
                <option value="name">Name</option>
                <option value="count">Count</option>
            </select>

            {data.map(item => <Item key={item.id}
                                    id={item.id}
                                    imageUrl={item.imageUrl}
                                    count={item.count}
                                    name={item.name}
                                    size={item.size}
                                    weight={item.weight}
                                    deleteItem={deleteItem}
            />)}
            <AddProductReduxForm/>
        </div>

    )
}


export default MainPage
