import s from './Item.module.css'
import {NavLink} from "react-router-dom";

const Item = ({imageUrl, count, name, size, weight,id,deleteItem}) => {


    const confirmDelete = (id) => {
        let result  = window.confirm('Are you sure?')
        if(result){
          deleteItem(id)
        } else {
            return null
        }

    }

    return (
        <div>
            <div>
                 <table className={s.item}>
                     <tbody>
                        <tr>
                            <td><img src={imageUrl} alt=''/>
                            </td>
                            <td valign="top">
                                name: {name}
                                <br/>
                                description: {weight + '' + size.height + '' + size.height}
                                <br/>
                                count: {count}
                                <br/>
                                <NavLink to='/additionalInformation/ '>Additional information</NavLink>
                            </td>
                            <td>
                                <button onClick={() => confirmDelete(id)}>X</button>
                            </td>
                        </tr>
                     </tbody>
                    </table>

            </div>
        </div>
    )

}


export default Item
