import Item from "./Items/Item";
import s from './MainPage.module.css'
import {useState} from "react";

const MainPage = ({items}) => {
let sortDirection = false
    let state = [...items]

    function sortColumn(columnName) {
    debugger
        const dataType = typeof state[0]['id']
        sortDirection = !sortDirection
        switch (dataType) {
            case 'string':
                sortStringColumn(sortDirection, columnName)
        }
    }

    function sortStringColumn(sort, columnName) {
        state = state.sort((a, b) => {
            if (sort) {
                return a[columnName] > b[columnName] ? 1 : -1
            } else {
                return a[columnName] < b[columnName] ? 1 : -1
            }
        })
    }
    function sortByName(a, b, sortAsc = true) {
        var val1 = a.name;
        var val2 = b.name;
        let result = ((val1 < val2) ? -1 : ((val1 > val2) ? 1 : 0));
        if (!sortAsc) {
            result *= -1;
        }
        return result;
    }
    return (
        <div>
            <div>
                <span >Name</span>
                <div onClick={() => sortColumn('name')}>Count</div>
            </div>
            {items.map(item => <Item key={item.id}
                                     imageUrl={item.imageUrl}
                                     count={item.count}
                                     name={item.name}
                                     size={item.size}
                                     weight={item.weight}
            />)}
        </div>
    )
}

export default MainPage
