import React from 'react'
import {connect} from "react-redux";
import AddInformation from "./AddInformation";
import {withRouter} from "react-router-dom";
import {compose} from "redux";

class AddInformationContainer extends React.Component {

    state = {
        item: null
    }

    getItemId = () => {
        let itemId = this.props.match.params.itemId - 1
        let item = this.props.items[itemId]
        this.setState({
            item: item
        })
    }
    componentDidMount() {
        this.getItemId()
    }
    // componentDidUpdate(prevProps, prevState, snapshot) {
    //     if(prevState !== this.state){
    //         this.getItemId()
    //     }
    // }

    render() {
       return(
           <>

                <AddInformation imgUrl={this.state.imgUrl}
                                name={this.state.name}
                                description={this.state.description}
                                collar={this.state.collar}
                                size={this.state.size}
                                weight={this.state.weight} />
           </>
       )
   }
}

const mapStateToProps = (state) => ({
    imgUrl: state.itemsPage.items.imageUrl,
    name: state.itemsPage.items.name,
    description: state.itemsPage.items.description,
    collar: state.itemsPage.items.collar,
    size: state.itemsPage.items.size,
    weight: state.itemsPage.items.weight,
    items: state.itemsPage.items


})
const mapDispatchToProps = (state) => {

}



export default compose(connect(mapStateToProps, mapDispatchToProps),
    withRouter)(AddInformationContainer)
