        }
    ]

}

export const itemsReducer = (state = initialState, action) => {
    switch (action.type) {
        case DELETE_ITEM :
            return {
                ...state,
                items: state.items.filter(item => item.id !== action.itemId)
            }
        case ADD_ITEM:
            let item = {
                name: '',
                description: '',

            }
            return {
                ...state,
                items: state.items.push()
            }
        default:
            return state
    }
}

export const deleteItem = (itemId) => ({type: 'DELETE_ITEM', itemId})
export const addItem = (data) => ({type: 'DELETE_ITEM', data})
