import React from 'react'
import {connect} from "react-redux";
import AddInformation from "./AddInformation";
import {withRouter} from "react-router-dom";
import {compose} from "redux";
import {setItem} from "../../Redux/items-reducer";

class AddInformationContainer extends React.Component {



    getItemId = () => {
        let itemId = this.props.match.params.itemId
        let obj = this.props.items[itemId]
        console.log(obj)
        this.props.setItem(obj)
    }
    componentDidMount() {
        this.getItemId()
    }
    componentDidUpdate(prevProps, prevState, snapshot) {
        if(prevProps.item !== this.props.item){
            this.getItemId()
        }
    }

    render() {
       return(
           <>
                {/*<AddInformation item={this.props.item}*/}
                />
           </>
       )
   }
}

const mapStateToProps = (state) => ({
    // imgUrl: state.itemsPage.items.imageUrl,
    // name: state.itemsPage.items.name,
    // description: state.itemsPage.items.description,
    // collar: state.itemsPage.items.collar,
    // size: state.itemsPage.items.size,
    // weight: state.itemsPage.items.weight,
    item: state.itemsPage.item,
    items: state.itemsPage.items,
})



export default compose(connect(mapStateToProps, {setItem}),
    withRouter)(AddInformationContainer)
