import Item from "./Items/Item";
import {useState} from "react";

const MainPage = ({items}) => {


    let state = useState([...items])
    const sortByName = () => {
        debugger
        state.sort(function (a, b) {
            if (a.name > b.name) {
                return 1
            }
            if (a.name < b.name) {
                return -1
            }
            return 0
        })
    }


    return (
        <div>
            <div>
                <span>Name</span>
                <span onClick={sortByName}>Count</span>
            </div>
            {state.map(item => <Item key={state.id}
                                     imageUrl={state.imageUrl}
                                     count={state.count}
                                     name={state.name}
                                     size={state.size}
                                     weight={state.weight}
            />)}
        </div>
    )
}

export default MainPage
